import React from 'react';
import { useLocation } from 'react-router-dom';

function FoodChoices() {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const preference = queryParams.get('preference');

  // Define food options based on dietary preference
  const foodOptions = {
    Vegetarian: ['Salad', 'Tofu Stir-fry', 'Vegetable Soup'],
    'Gluten Free': ['Quinoa Salad', 'Rice Noodles', 'Grilled Chicken'],
    'Dairy Free': ['Almond Milk Smoothie', 'Fruit Salad', 'Grilled Fish'],
    Keto: ['Avocado Salad', 'Bacon-Wrapped Asparagus', 'Cheese Platter'],
    Other: ['Mixed Plate', 'Chef’s Special'],
  };

  return (
    <div className="food-choices-container">
      <h1>Food Choices for {preference}</h1>
      <ul>
        {foodOptions[preference]?.map((food, index) => (
          <li key={index} className="food-choice-item">{food}</li>
        ))}
      </ul>
    </div>
  );
}

export default FoodChoices;
