import React, { useState } from 'react';
import { ReactTyped } from 'react-typed';
import PasswordStrengthBar from 'react-password-strength-bar';
import './SignUp.css';

function SignUp() {
  const [currentStep, setCurrentStep] = useState(0);
  const [email, setEmail] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [username, setUsername] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showInput, setShowInput] = useState(false);

  const steps = [
    {
      label: 'Enter your email*',
      input: (
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="a1112@gmail.com"
          required
        />
      ),
    },
    {
      label: 'Create a password*',
      input: (
        <>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Create a Password"
            required
          />
          <PasswordStrengthBar password={password} />
        </>
      ),
    },
    {
      label: 'Confirm Password',
      input: (
        <input
          type="password"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          placeholder="Confirm Your Password"
          required
        />
      ),
    },
    {
      label: 'Mobile Number',
      input: (
        <input
          type="tel"
          value={mobileNumber}
          onChange={(e) => setMobileNumber(e.target.value)}
          placeholder="Your Mobile Number"
          required
        />
      ),
    },
    {
      label: 'Enter a Username',
      input: (
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Choose a Username"
          required
        />
      ),
    },
  ];

  const handleNext = () => {
    if (currentStep < steps.length) {
      if (currentStep === 0 && !email) {
        setError('Email is required.');
        return;
      }
      if (currentStep === 1 && !password) {
        setError('Password is required.');
        return;
      }
      if (currentStep === 2) {
        if (password !== confirmPassword) {
          setError('Passwords do not match.');
          return;
        }
      }

      // Clear error if there are no issues
      setError('');
      if (currentStep < steps.length - 1) {
        setCurrentStep((prevStep) => prevStep + 1);
      } else {
        setSuccess('Registration successful!');
        // Here you could also handle any registration logic, e.g., API call
      }
    }
  };

  return (
    <div className="signup-container">
      {!showInput && (
        <h1>
          <ReactTyped
            strings={["Welcome to GitHub!", "Let’s begin the adventure"]}
            typeSpeed={50}
            backSpeed={30}
            loop={false}
            onComplete={() => setShowInput(true)}
            smartBackspace={true}
            showCursor={false}
            cursorChar='_'
          />
        </h1>
      )}
      {showInput && (
        <>
          <h1>{steps[currentStep].label}</h1>
          <form className="signup-form" onSubmit={(e) => e.preventDefault()}>
            <label>
              {steps[currentStep].input}
            </label>
            {error && <p className="error-message">{error}</p>}
            {success && <p className="success-message">{success}</p>}
            <button type="button" className="signup-button" onClick={handleNext}>
              {currentStep === steps.length - 1 ? 'Register' : 'Continue'}
            </button>
          </form>
        </>
      )}
    </div>
  );
}

export default SignUp;
