import React, { useState } from 'react';
import './MedicalCondition.css';
import { useNavigate } from 'react-router-dom';

const MedicalConditions = () => {
  const navigate = useNavigate();
  const [selectedCondition, setSelectedCondition] = useState('');
  const [selectedAllergy, setSelectedAllergy] = useState('');

  const handleSelectCondition = (condition) => {
    setSelectedCondition(condition);
  };

  const handleSelectAllergy = (allergy) => {
    setSelectedAllergy(allergy);
  };

  const handleProceed = () => {
    // Navigate to Nutrition Details page and pass selected conditions and allergies
    navigate('/nutrition-details', { state: { medicalCondition: selectedCondition, allergy: selectedAllergy } });
  };

  return (
    <div className="medical-conditions-container">
      <h1>Medical Conditions</h1>

      <div className="button-group">
        <button
          className={selectedCondition === 'Diabetes' ? 'selected' : ''}
          onClick={() => handleSelectCondition('Diabetes')}
        >
          Diabetes
        </button>

        <button
          className={selectedCondition === 'Hypertension' ? 'selected' : ''}
          onClick={() => handleSelectCondition('Hypertension')}
        >
          Hypertension
        </button>

        <button
          className={selectedCondition === 'High Cholesterol' ? 'selected' : ''}
          onClick={() => handleSelectCondition('High Cholesterol')}
        >
          High Cholesterol
        </button>

        <button
          className={selectedAllergy === 'Allergies' ? 'selected' : ''}
          onClick={() => handleSelectAllergy('Allergies')}
        >
          Allergies
        </button>

        <button
          className={selectedCondition === 'Other' ? 'selected' : ''}
          onClick={() => handleSelectCondition('Other')}
        >
          Other (Specify)
        </button>
      </div>

      <button className="proceed-button" onClick={handleProceed}>
        Proceed
      </button>
    </div>
  );
};

export default MedicalConditions;
