import React, { useState } from 'react';
import { Routes, Route, useLocation } from 'react-router-dom';
import PersonalizeExperience from './PersonalizeExperience';
import GenderSelection from './GenderSelection';
import AgeSelection from './AgeSelection';
import ProgressIndicator from '../components/ProgressIndicator';

function App() {
  const [completedSteps, setCompletedSteps] = useState([false, false, false, false]); // Initialize all steps as incomplete
  const location = useLocation();

  return (
    <div>
      {location.pathname === '/' && (
        <ProgressIndicator completedSteps={completedSteps} />
      )}
      <Routes>
        <Route path="/" element={<PersonalizeExperience />} />
        <Route path="/gender" element={<GenderSelection setCompletedSteps={setCompletedSteps} />} />
        <Route path="/age" element={<AgeSelection completedSteps={completedSteps} setCompletedSteps={setCompletedSteps} />} />
        {/* Add routes for WeightSelection and HeightSelection similarly */}
      </Routes>
    </div>
  );
}

export default App;
