import React, { useState } from 'react';
import ReactTyped from 'react-typed';
import PasswordStrengthBar from 'react-password-strength-bar';
import './SignUp.css';

function SignUp() {
  const [currentStep, setCurrentStep] = useState(0);
  const [showInput, setShowInput] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [username, setUsername] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const steps = [
    { label: 'Enter your email*', input: <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required /> },
    { 
      label: 'Create a password*', 
      input: (
        <div style={{ position: 'relative' }}>
          <input
            type={showPassword ? 'text' : 'password'}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button 
            type="button" 
            onClick={() => setShowPassword(!showPassword)} 
            style={{ position: 'absolute', right: '10px', top: '10px', cursor: 'pointer' }}
          >
            {showPassword ? 'Hide' : 'Show'}
          </button>
          <PasswordStrengthBar password={password} />
        </div>
      ),
    },
    { 
      label: 'Confirm Password', 
      input: (
        <div style={{ position: 'relative' }}>
          <input
            type={showPassword ? 'text' : 'password'}
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
          />
          <button 
            type="button" 
            onClick={() => setShowPassword(!showPassword)} 
            style={{ position: 'absolute', right: '10px', top: '10px', cursor: 'pointer' }}
          >
            {showPassword ? 'Hide' : 'Show'}
          </button>
        </div>
      ),
    },
    { label: 'Full Name', input: <input type="text" value={fullName} onChange={(e) => setFullName(e.target.value)} required /> },
    { label: 'Mobile Number', input: <input type="tel" value={mobileNumber} onChange={(e) => setMobileNumber(e.target.value)} required /> },
    { label: 'Enter a Username', input: <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} required /> },
  ];

  const handleNext = () => {
    // Validate the current step
    if (currentStep === 0 && !email) {
      setError('Email is required.');
      return;
    }
    if (currentStep === 1 && !password) {
      setError('Password is required.');
      return;
    }
    if (currentStep === 2 && password !== confirmPassword) {
      setError('Passwords do not match.');
      return;
    }
    if (currentStep === 3 && !fullName) {
      setError('Full Name is required.');
      return;
    }
    if (currentStep === 4 && !mobileNumber) {
      setError('Mobile number is required.');
      return;
    }
    if (currentStep === 5 && !username) {
      setError('Username is required.');
      return;
    }

    // Clear error and move to the next step
    setError('');
    if (currentStep < steps.length - 1) {
      setCurrentStep((prevStep) => prevStep + 1);
    } else {
      handleSubmit();
    }
  };

  const handleSubmit = async () => {
    try {
      const userData = {
        fullName,
        email,
        mobileNumber,
        username,
        password,
      };

      const response = await fetch('http://localhost:5000/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setError(errorData.message || 'Registration failed.');
        return;
      }

      const data = await response.json();
      setSuccess(data.message);
    } catch (error) {
      setError('An error occurred during registration.');
    }
  };

  return (
    <div className="signup-container">
      {!showInput ? (
        <h1>
          <ReactTyped
            strings={["Welcome to GitHub!", "Letâ€™s begin the adventure"]}
            typeSpeed={50}
            backSpeed={30}
            loop={false}
            onComplete={() => setShowInput(true)}
          />
        </h1>
      ) : (
        <>
          <h1>{steps[currentStep].label}</h1>
          <form className="signup-form" onSubmit={(e) => e.preventDefault()}>
            {steps[currentStep].input}
            {error && <p className="error-message">{error}</p>}
            {success && <p className="success-message">{success}</p>}
            <button type="button" className="signup-button" onClick={handleNext}>
              {currentStep === steps.length - 1 ? 'Register' : 'Continue'}
            </button>
          </form>
        </>
      )}
    </div>
  );
}

export default SignUp;
