import React, { useState } from 'react';
import WeightSelection from './WeightSelection';
import HeightSelection from './HeightSelection';
import BmiResult from './Bmi';

const App = () => {
    const [weight, setWeight] = useState(null);
    const [height, setHeight] = useState(null);
    const [bmi, setBmi] = useState(null);
    const [error, setError] = useState(null);

    const handleWeightSelect = (selectedWeight) => {
        setWeight(selectedWeight);
        setBmi(null); // Reset BMI when weight changes
    };

    const handleHeightSelect = (selectedHeight) => {
        setHeight(selectedHeight);
        calculateBmi(weight, selectedHeight);
    };

    const calculateBmi = (weight, height) => {
        if (weight && height) {
            const heightInMeters = height / 100;
            const calculatedBmi = weight / (heightInMeters * heightInMeters);
            setBmi(calculatedBmi);
            setError(null); // Clear any previous errors
        } else {
            setError("Please select both weight and height.");
        }
    };

    return (
        <div>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {bmi === null ? (
                <>
                    {weight === null ? (
                        <WeightSelection onWeightSelect={handleWeightSelect} />
                    ) : (
                        <HeightSelection onHeightSelect={handleHeightSelect} selectedWeight={weight} />
                    )}
                </>
            ) : (
                <BmiResult bmi={bmi} />
            )}
        </div>
    );
};

export default App;
