import React, { useState } from 'react';
import PasswordStrengthBar from 'react-password-strength-bar';
import './SignUp.css';

function SignUp() {
  const [step, setStep] = useState(1); // Track the current step
  const [fullName, setFullName] = useState(''); // Full name state
  const [email, setEmail] = useState('');
  const [mobileNumber, setMobileNumber] = useState(''); // Mobile number state
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [username, setUsername] = useState(''); // Full name state

  const handleContinue = () => {
    setError('');

    // Validation logic for each step
    if (step === 1 && !fullName) {
      setError('Please enter your full name.');
      return;
    }

    if (step === 2 && !email) {
      setError('Please enter a valid email.');
      return;
    }

    if (step === 3 && !mobileNumber) {
      setError('Please enter a valid mobile number.');
      return;
    }

    if (step === 4 && !password) {
      setError('Please enter a valid password.');
      return;
    }

    if (step === 5 && password !== confirmPassword) {
      setError('Passwords do not match.');
      return;
    }

    setStep(step + 1); // Move to the next step
  };

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent the form from submitting normally

    try {
      const userData = {
        fullName,
        email,
        mobileNumber,
        password,
      };
      console.log('Submitting user data:', userData); // Log the data being sent

      const response = await fetch('http://localhost:5000/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        console.error('Registration error:', errorData); // Log the error response
        setError(errorData.message || 'Registration failed.');
        return;
      }

      const data = await response.json();
      setSuccess(data.message);
      setError(''); // Clear any existing errors
    } catch (error) {
      console.error('Fetch error:', error);
      setError('An error occurred during registration.');
    }
  };

  return (
    <div className="signup-container">
      <div className="signup-form-container">
        <form className="signup-form" onSubmit={handleSubmit}>
          {/* Step 1: Full Name */}
          <div className="form-group">
            <label>Enter your full name*</label>
            <div className="input-group">
              <input
                type="text"
                value={fullName}
                onChange={(e) => setFullName(e.target.value)}
                required
                disabled={step > 1}
              />
              {step === 1 && (
                <button
                  type="button"
                  className="continue-button"
                  onClick={handleContinue}
                >
                  Continue
                </button>
              )}
            </div>
          </div>
{/* Step 2: Enter Username */}
{step >= 2 && (
  <div className="form-group">
    <label>Choose a username*</label>
    <div className="input-group">
      <input
        type="text"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        required
        disabled={step > 2}
      />
      {step === 2 && (
        <button
          type="button"
          className="continue-button"
          onClick={handleContinue}
        >
          Continue
        </button>
      )}
    </div>
  </div>
)}

          {/* Step 3: Enter Email */}
          {step >= 3 && (
            <div className="form-group">
              <label>Enter your email*</label>
              <div className="input-group">
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  disabled={step > 3}
                />
                {step === 3 && (
                  <button
                    type="button"
                    className="continue-button"
                    onClick={handleContinue}
                  >
                    Continue
                  </button>
                )}
              </div>
            </div>
          )}

          {/* Step 4: Mobile Number */}
          {step >= 4 && (
            <div className="form-group">
              <label>Enter your mobile number*</label>
              <div className="input-group">
                <input
                  type="text"
                  value={mobileNumber}
                  onChange={(e) => setMobileNumber(e.target.value)}
                  required
                  disabled={step > 4}
                />
                {step === 4 && (
                  <button
                    type="button"
                    className="continue-button"
                    onClick={handleContinue}
                  >
                    Continue
                  </button>
                )}
              </div>
            </div>
          )}

          {/* Step 5: Show Password Field */}
          {step >= 5 && (
            <div className="form-group">
              <label>Create a password*</label>
              <div className="input-group">
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
                {step === 5 && (
                  <button
                    type="button"
                    className="continue-button"
                    onClick={handleContinue}
                  >
                    Continue
                  </button>
                )}
              </div>
            </div>
          )}

          {/* Step 6: Confirm Password */}
          {step >= 6 && (
            <div className="form-group">
              <label>Confirm Password*</label>
              <div className="input-group">
                <input
                  type="password"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  required
                />
                <button type="submit" className="continue-button">
                  Submit
                </button>
              </div>
            </div>
          )}

          {/* Error Message */}
          {error && <p className="error-message">{error}</p>}
          {success && <p className="success-message">{success}</p>}
        </form>
      </div>

      {/* Password Strength Bar should appear below the form */}
      {step >= 4 && (
        <div className="password-strength-container">
          <PasswordStrengthBar password={password} />
        </div>
      )}
    </div>
  );
}

export default SignUp;
