import React, { useState } from 'react';
import PasswordStrengthBar from 'react-password-strength-bar';
import { ReactTyped } from 'react-typed';
import './SignUp.css';

function SignUp() {
  const [currentStep, setCurrentStep] = useState(0);
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [username, setUsername] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showInput, setShowInput] = useState(false);

  const steps = [
    { label: 'Enter your email*', type: 'email', value: email, setter: setEmail },
    { label: 'Create a password*', type: 'password', value: password, setter: setPassword },
    { label: 'Confirm Password', type: 'password', value: confirmPassword, setter: setConfirmPassword },
    { label: 'Full Name', type: 'text', value: fullName, setter: setFullName },
    { label: 'Mobile Number', type: 'tel', value: mobileNumber, setter: setMobileNumber },
    { label: 'Enter a Username', type: 'text', value: username, setter: setUsername },
  ];

  const handleNext = () => {
    // Validate the current step
    if (currentStep === 0 && !email) {
      setError('Email is required.');
      return;
    }
    if (currentStep === 1 && !password) {
      setError('Password is required.');
      return;
    }
    if (currentStep === 2 && password !== confirmPassword) {
      setError('Passwords do not match.');
      return;
    }
    if (currentStep === 3 && !fullName) {
      setError('Full Name is required.');
      return;
    }
    if (currentStep === 4 && !mobileNumber) {
      setError('Mobile number is required.');
      return;
    }
    if (currentStep === 5 && !username) {
      setError('Username is required.');
      return;
    }

    // Clear error and move to the next step
    setError('');
    setCurrentStep((prevStep) => prevStep + 1);

    // Clear password fields when moving to the next step
    if (currentStep === 1) {
      setPassword('');
      setConfirmPassword('');
    }
  };

  const handleSubmit = async () => {
    try {
      const userData = {
        fullName,
        email,
        mobileNumber,
        username,
        password,
      };
      console.log('Submitting user data:', userData); // Log the data being sent

      const response = await fetch('http://localhost:5000/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        console.error('Registration error:', errorData); // Log the error response
        setError(errorData.message || 'Registration failed.');
        return;
      }

      const data = await response.json();
      setSuccess(data.message);
    } catch (error) {
      console.error('Fetch error:', error);
      setError('An error occurred during registration.');
    }
  };

  return (
    <div className="signup-container">
      {!showInput ? (
        <h1>
          <ReactTyped
            strings={["Welcome to GitHub!", "Letâ€™s begin the adventure"]}
            typeSpeed={50}
            backSpeed={30}
            loop={false}
            onComplete={() => setShowInput(true)}
          />
        </h1>
      ) : (
        <div className="signup-form-container">
          <form className="signup-form" onSubmit={(e) => e.preventDefault()}>
            {/* Render the current step */}
            <div style={{ display: 'block' }}>
              <label>{steps[currentStep].label}</label>
              <div className="input-group">
                <input
                  type={steps[currentStep].type}
                  value={steps[currentStep].value}
                  onChange={(e) => steps[currentStep].setter(e.target.value)}
                  required
                />
                {/* Only show "Continue" button if not on the last step */}
                {currentStep < steps.length - 1 && (
                  <button
                    type="button"
                    className="signup-button"
                    onClick={handleNext}
                  >
                    Continue
                  </button>
                )}
              </div>
            </div>

            {error && <p className="error-message">{error}</p>}
            {success && <p className="success-message">{success}</p>}

            {currentStep === steps.length - 1 && (
              <div className="signup-form-button-container">
                <button type="button" className="signup-button" onClick={handleSubmit}>
                  Register
                </button>
              </div>
            )}

            {/* Show password strength bar only on the password step */}
            {currentStep === 1 && (
              <div className="password-strength-container">
                <PasswordStrengthBar password={password} />
              </div>
            )}
          </form>
        </div>
      )}
    </div>
  );
}

export default SignUp;
