import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './SignUp.css';

const typingEffect = (message, setText) => {
  let index = 0;
  const typingInterval = setInterval(() => {
    if (index < message.length) {
      setText((prev) => prev + message[index]);
      index++;
    } else {
      clearInterval(typingInterval);
    }
  }, 100); // Adjust typing speed here (in milliseconds)
};

function SignUp() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [welcomeMessage, setWelcomeMessage] = useState('');
  const [showInput, setShowInput] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    const message = "Welcome to GitHub!\nLet’s begin the adventure";
    setWelcomeMessage(''); // Reset the welcome message
    typingEffect(message, setWelcomeMessage);

    const timeoutId = setTimeout(() => setShowInput(true), message.length * 100 + 500); // Show input after message
    return () => clearTimeout(timeoutId);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Basic email validation
    if (!email) {
      setError('Email is required.');
      return;
    }

    // Simulate successful registration
    setSuccess('Registration successful!');
    setError('');
    setEmail(''); // Reset email input
    // navigate('/personalize'); // Uncomment to navigate to another page
  };

  return (
    <div className="signup-container">
      <h1>{welcomeMessage}</h1>
      {showInput && (
        <form className="signup-form" onSubmit={handleSubmit}>
          <label>
            Enter your email*
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="Your Email Address"
            />
          </label>
          {error && <p className="error-message">{error}</p>}
          {success && <p className="success-message">{success}</p>}
          <button type="submit" className="signup-button">Continue</button>
        </form>
      )}
    </div>
  );
}

export default SignUp;
