import React, { useState } from 'react';
import PasswordStrengthBar from 'react-password-strength-bar';
import './SignUp.css';

function SignUp() {
  const [step, setStep] = useState(1); // This keeps track of which step the user is on
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Handles the next step and basic validation
  const handleContinue = () => {
    // Reset any previous error message
    setError('');

    if (step === 1 && !email) {
      setError('Please enter a valid email.');
      return;
    }

    if (step === 2 && !password) {
      setError('Please enter a valid password.');
      return;
    }

    if (step === 3 && password !== confirmPassword) {
      setError('Passwords do not match.');
      return;
    }

    // Move to the next step
    setStep(step + 1);
  };

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent form default submission (reload)

    const userData = { email, password };

    try {
      const response = await fetch('http://localhost:5000/api/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(userData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setError(errorData.message || 'Registration failed.');
        return;
      }

      const data = await response.json();
      setSuccess(data.message);
    } catch {
      setError('An error occurred during registration.');
    }
  };

  return (
    <div className="signup-container">
      <div className="signup-form-container">
        <form className="signup-form" onSubmit={handleSubmit}>

          {/* Step 1: Enter Email */}
          {step === 1 && (
            <div>
              <label>Enter your email*</label>
              <div className="input-group">
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </div>
              <button type="button" className="signup-button" onClick={handleContinue}>
                Continue
              </button>
            </div>
          )}

          {/* Step 2: Create Password */}
          {step === 2 && (
            <div>
              <label>Create a password*</label>
              <div className="input-group">
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <PasswordStrengthBar password={password} />
              <button type="button" className="signup-button" onClick={handleContinue}>
                Continue
              </button>
            </div>
          )}

          {/* Step 3: Confirm Password */}
          {step === 3 && (
            <div>
              <label>Confirm Password*</label>
              <div className="input-group">
                <input
                  type="password"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  required
                />
              </div>
              <button type="submit" className="signup-button">
                Submit
              </button>
            </div>
          )}

          {error && <p className="error-message">{error}</p>}
          {success && <p className="success-message">{success}</p>}
        </form>
      </div>
    </div>
  );
}

export default SignUp;
