import React, { useState } from 'react';
import { ReactTyped } from 'react-typed';
import PasswordStrengthBar from 'react-password-strength-bar';
import './SignUp.css';

function SignUp() {
  const [currentStep, setCurrentStep] = useState(0);
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [username, setUsername] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showInput, setShowInput] = useState(false);

  const steps = [
    { label: 'Enter your email*', input: <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="a1112@gmail.com" required /> },
    { label: 'Create a password*', input: <><input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Create a Password" required /><PasswordStrengthBar password={password} /></> },
    { label: 'Confirm Password', input: <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} placeholder="Confirm Your Password" required /> },
    { label: 'Full Name', input: <input type="text" value={fullName} onChange={(e) => setFullName(e.target.value)} placeholder="Your Full Name" required /> },
    { label: 'Mobile Number', input: <input type="tel" value={mobileNumber} onChange={(e) => setMobileNumber(e.target.value)} placeholder="Your Mobile Number" required /> },
    { label: 'Enter a Username', input: <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Choose a Username" required /> },
  ];

  const handleNext = () => {
    setError('');

    switch (currentStep) {
      case 0:
        if (!email) return setError('Email is required.');
        break;
      case 1:
        if (!password) return setError('Password is required.');
        break;
      case 2:
        if (password !== confirmPassword) return setError('Passwords do not match.');
        break;
      case 3:
        if (!fullName) return setError('Full Name is required.');
        break;
      case 4:
        if (!mobileNumber) return setError('Mobile number is required.');
        break;
      case 5:
        if (!username) return setError('Username is required.');
        break;
      default:
        break;
    }

    if (currentStep === steps.length - 1) {
      handleSubmit();
    } else {
      setCurrentStep((prevStep) => prevStep + 1);
    }
  };

  const handleSubmit = async () => {
    // Your submission logic here
    // ...
  };

  return (
    <div className="signup-container">
      {!showInput ? (
        <h1>
          <ReactTyped strings={["Welcome to GitHub!", "Letâ€™s begin the adventure"]} typeSpeed={50} backSpeed={30} loop={false} onComplete={() => setShowInput(true)} />
        </h1>
      ) : (
        <>
          <h1>{steps[currentStep].label}</h1>
          <form className="signup-form" onSubmit={(e) => e.preventDefault()}>
            {steps[currentStep].input}
            {error && <p className="error-message">{error}</p>}
            <button type="button" className="signup-button" onClick={handleNext}>
              {currentStep === steps.length - 1 ? 'Register' : 'Continue'}
            </button>
          </form>
        </>
      )}
    </div>
  );
}

export default SignUp;
