import React, { useState } from 'react';
import PasswordStrengthBar from 'react-password-strength-bar';
import './SignUp.css';

function SignUp() {
  const [currentStep, setCurrentStep] = useState(0);
  const [fullName, setFullName] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const steps = [
    { label: 'Email', input: (
      <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Your Email Address"
        required
      />
    )},
    { label: 'Full Name', input: (
      <input
        type="text"
        value={fullName}
        onChange={(e) => setFullName(e.target.value)}
        placeholder="Your Full Name"
        required
      />
    )},
    { label: 'Mobile Number', input: (
      <input
        type="tel"
        value={mobileNumber}
        onChange={(e) => setMobileNumber(e.target.value)}
        placeholder="Your Mobile Number"
        required
      />
    )},
    { label: 'Create Password', input: (
      <>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Create a Password"
          required
        />
        <PasswordStrengthBar password={password} />
      </>
    )},
    { label: 'Confirm Password', input: (
      <input
        type="password"
        value={confirmPassword}
        onChange={(e) => setConfirmPassword(e.target.value)}
        placeholder="Confirm Your Password"
        required
      />
    )},
    { label: 'Enter a Username', input: (
      <input
        type="text"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        placeholder="Choose a Username"
        required
      />
    )}
  ];

  const handleNext = () => {
    if (currentStep === steps.length - 1) {
      if (password !== confirmPassword) {
        setError('Passwords do not match.');
        return;
      }
      setSuccess('Registration successful!');
      setError('');
      // Handle registration logic here
    } else {
      setCurrentStep((prevStep) => prevStep + 1);
    }
  };

  return (
    <div className="signup-container">
      <h1 className="signup-title">{steps[currentStep].label}</h1>
      <form className="signup-form" onSubmit={(e) => e.preventDefault()}>
        <label className="signup-label">
          {steps[currentStep].input}
        </label>
        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">{success}</p>}
        <button type="button" className="signup-button" onClick={handleNext}>
          {currentStep === steps.length - 1 ? 'Register' : 'Continue'}
        </button>
      </form>
    </div>
  );
}

export default SignUp;
