import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import PasswordStrengthBar from 'react-password-strength-bar';
import './SignUp.css';

function SignUp() {
  const navigate = useNavigate();
  const [fullName, setFullName] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [currentStep, setCurrentStep] = useState(0);

  const steps = [
    {
      label: 'Enter your full name',
      input: (
        <input
          type="text"
          value={fullName}
          onChange={(e) => setFullName(e.target.value)}
          placeholder="Full Name"
          required
        />
      ),
    },
    {
      label: 'Enter your mobile number',
      input: (
        <input
          type="text"
          value={mobileNumber}
          onChange={(e) => setMobileNumber(e.target.value)}
          placeholder="Mobile Number"
          required
        />
      ),
    },
    {
      label: 'Enter your email',
      input: (
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
        />
      ),
    },
    {
      label: 'Choose a username',
      input: (
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Username"
          required
        />
      ),
    },
    {
      label: 'Create a password',
      input: (
        <>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            required
          />
          <PasswordStrengthBar password={password} />
        </>
      ),
    },
    {
      label: 'Confirm your password',
      input: (
        <input
          type="password"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          placeholder="Confirm Password"
          required
        />
      ),
    },
  ];

  const handleNext = async () => {
    // Validate password match at the confirm password step
    if (currentStep === 4) {
      if (password !== confirmPassword) {
        setError('Passwords do not match.');
        return;
      }
    }

    // Clear error if there are no issues
    setError('');

    if (currentStep < steps.length - 1) {
      setCurrentStep((prevStep) => prevStep + 1);
    } else {
      await handleSubmit();
    }
  };

  const handleSubmit = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          fullName,
          mobileNumber,
          email,
          username,
          password,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        setSuccess(data.message);
        navigate('/personalize'); // Redirect on success
      } else {
        const errorData = await response.json();
        setError(errorData.message || 'Error registering user.');
      }
    } catch (error) {
      console.error('Error:', error);
      setError('Failed to register. Please try again.');
    }
  };

  return (
    <div className="signup-container">
      <h1>{steps[currentStep].label}</h1>
      <form className="signup-form" onSubmit={(e) => e.preventDefault()}>
        {steps[currentStep].input}
        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">{success}</p>}
        <button type="button" className="signup-button" onClick={handleNext}>
          {currentStep === steps.length - 1 ? 'Register' : 'Continue'}
        </button>
      </form>
    </div>
  );
}

export default SignUp;
