import React, { useState } from 'react';
import PasswordStrengthBar from 'react-password-strength-bar';
import './SignUp.css';

function SignUp() {
  const [currentStep, setCurrentStep] = useState(0);
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [username, setUsername] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [showInput, setShowInput] = useState(false);

  const steps = [
    { label: 'Enter your email*', type: 'email', value: email, setter: setEmail },
    { label: 'Create a password*', type: 'password', value: password, setter: setPassword },
    { label: 'Confirm Password', type: 'password', value: confirmPassword, setter: setConfirmPassword },
    { label: 'Full Name', type: 'text', value: fullName, setter: setFullName },
    { label: 'Mobile Number', type: 'tel', value: mobileNumber, setter: setMobileNumber },
    { label: 'Enter a Username', type: 'text', value: username, setter: setUsername },
  ];

  const handleNext = () => {
    // Validate the current step
    if (currentStep === 0 && !email) {
      setError('Email is required.');
      return;
    }
    if (currentStep === 1 && !password) {
      setError('Password is required.');
      return;
    }
    if (currentStep === 2 && password !== confirmPassword) {
      setError('Passwords do not match.');
      return;
    }
    if (currentStep === 3 && !fullName) {
      setError('Full Name is required.');
      return;
    }
    if (currentStep === 4 && !mobileNumber) {
      setError('Mobile number is required.');
      return;
    }
    if (currentStep === 5 && !username) {
      setError('Username is required.');
      return;
    }

    // Clear error and move to the next step
    setError('');
    setCurrentStep((prevStep) => prevStep + 1);
  };

  const handleSubmit = async () => {
    try {
      const userData = {
        fullName,
        email,
        mobileNumber,
        username,
        password,
      };
      console.log('Submitting user data:', userData); // Log the data being sent

      const response = await fetch('http://localhost:5000/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        console.error('Registration error:', errorData); // Log the error response
        setError(errorData.message || 'Registration failed.');
        return;
      }

      const data = await response.json();
      setSuccess(data.message);
    } catch (error) {
      console.error('Fetch error:', error);
      setError('An error occurred during registration.');
    }
  };

  const checkPasswordStrength = () => {
    if (password.length < 8) {
      setPasswordError('Password is too short');
    } else {
      setPasswordError('');
    }
  };

  return (
    <div className="signup-container">
      <h1>Create Your Account</h1>
      <form className="signup-form" onSubmit={(e) => e.preventDefault()}>
        {steps.map((step, index) => (
          <div key={index} style={{ display: index <= currentStep ? 'block' : 'none' }}>
            <label>{step.label}</label>
            <input
              type={step.type}
              value={step.value}
              onChange={(e) => {
                step.setter(e.target.value);
                if (step.type === 'password') {
                  checkPasswordStrength();
                }
              }}
              required
            />
          </div>
        ))}
        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">{success}</p>}
        {currentStep < steps.length - 1 ? (
          <button type="button" className="signup-button" onClick={handleNext}>
            Continue
          </button>
        ) : (
          <button type="button" className="signup-button" onClick={handleSubmit}>
            Register
          </button>
        )}
      </form>
      {currentStep === 1 && (
        <div className="password-strength-container">
          <PasswordStrengthBar password={password} />
          <div className="password-validity-container">
            {passwordError && (
              <div className="js-continue-hint-container">
                <p id="password-err" data-hint-for="password" role="alert" aria-atomic="true">
                  <div className="password-validity-pill-container">
                    <span className="password-validity-pill password-validity-pill-fail mr-2 rounded-2"></span>
                    <span className="password-validity-pill mr-2 rounded-2"></span>
                    <span className="password-validity-pill rounded-2"></span>
                  </div>
                  <p className="password-validity-summary password-validity-summary-fail mb-1">
                    {passwordError}
                  </p>
                  <p>
                    Make sure it's <span data-more-than-n-chars="">at least 15 characters</span> OR 
                    <span data-min-chars="">at least 8 characters</span> 
                    <span data-number-requirement="">including a number</span> 
                    <span data-letter-requirement="">and a lowercase letter</span>.
                  </p>
                </p>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default SignUp;
