import React, { useState } from 'react';
import { ReactTyped } from 'react-typed';
import PasswordStrengthBar from 'react-password-strength-bar';
import './SignUp.css';

function SignUp() {
  const [currentStep, setCurrentStep] = useState(0);
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [username, setUsername] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showInput, setShowInput] = useState(false);

  const steps = [
    {
      label: 'Full Name*',
      input: (
        <input
          type="text"
          value={fullName}
          onChange={(e) => setFullName(e.target.value)}
          placeholder="Your Full Name"
          required
        />
      ),
    },
    {
      label: 'Enter your email*',
      input: (
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="a1112@gmail.com"
          required
        />
      ),
    },
    {
      label: 'Create a password*',
      input: (
        <>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Create a Password"
            required
          />
          <PasswordStrengthBar password={password} />
        </>
      ),
    },
    {
      label: 'Confirm Password',
      input: (
        <input
          type="password"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          placeholder="Confirm Your Password"
          required
        />
      ),
    },
    {
      label: 'Mobile Number',
      input: (
        <input
          type="tel"
          value={mobileNumber}
          onChange={(e) => setMobileNumber(e.target.value)}
          placeholder="Your Mobile Number"
          required
        />
      ),
    },
    {
      label: 'Enter a Username',
      input: (
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Choose a Username"
          required
        />
      ),
    },
  ];

  const handleNext = async () => {
    console.log('Current Step:', currentStep);
    console.log('Full Name:', fullName);
    console.log('Email:', email);
    console.log('Password:', password);
    console.log('Confirm Password:', confirmPassword);
    console.log('Mobile Number:', mobileNumber);
    console.log('Username:', username);

    if (currentStep < steps.length) {
      // Validate input based on current step
      if (currentStep === 0 && !fullName) {
        setError('Full Name is required.');
        return;
      }
      if (currentStep === 1 && !email) {
        setError('Email is required.');
        return;
      }
      if (currentStep === 2 && !password) {
        setError('Password is required.');
        return;
      }
      if (currentStep === 3 && password !== confirmPassword) {
        setError('Passwords do not match.');
        return;
      }
      if (currentStep === 4 && !mobileNumber) {
        setError('Mobile number is required.');
        return;
      }
      if (currentStep === 5 && !username) {
        setError('Username is required.');
        return;
      }

      // Clear error if all validations pass
      setError('');
      if (currentStep === steps.length - 1) {
        // Last step: submit the data
        await handleSubmit();
      } else {
        setCurrentStep((prevStep) => prevStep + 1);
      }
    }
  };

  const handleSubmit = async () => {
    try {
      const userData = {
        fullName,
        email,
        mobileNumber,
        username,
        password,
      };
      console.log('Submitting user data:', userData); // Log the data being sent

      const response = await fetch('http://localhost:5000/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        console.error('Registration error:', errorData); // Log the error response
        setError(errorData.message || 'Registration failed.');
        return;
      }

      const data = await response.json();
      setSuccess(data.message);
    } catch (error) {
      console.error('Fetch error:', error);
      setError('An error occurred during registration.');
    }
  };

  return (
    <div className="signup-container">
      {!showInput && (
        <h1>
          <ReactTyped
            strings={["Welcome to GitHub!", "Let’s begin the adventure"]}
            typeSpeed={50}
            backSpeed={30}
            loop={false}
            onComplete={() => setShowInput(true)}
            smartBackspace={true}
            showCursor={false}
            cursorChar='_'
          />
        </h1>
      )}
      {showInput && (
        <>
          <h1>{steps[currentStep].label}</h1>
          <form className="signup-form" onSubmit={(e) => e.preventDefault()}>
            <label>
              {steps[currentStep].input}
            </label>
            {error && <p className="error-message">{error}</p>}
            {success && <p className="success-message">{success}</p>}
            <button type="button" className="signup-button" onClick={handleNext}>
              {currentStep === steps.length - 1 ? 'Register' : 'Continue'}
            </button>
          </form>
        </>
      )}
    </div>
  );
}

export default SignUp;
